cryptoAESimport base64from Crypto.Cipher import AESfrom Crypto.Util.Padding import padimport hashlibdef encrypt_aes_cbc(plaintext, password, iv): key = hashlib.md5(password.encode()).digest()[:16] cipher = AES.new(key, AES.MODE_CBC, iv.encode()) ciphertext = cipher.encrypt(pad(plaintext.encode(&#x27;utf-8&#x27;), AES.block_size)) return base64.b64encode(ciphertext).decode(&#x27;utf-8&#x27;)plaintext = &quot;DASCTF&#123;**********&#125;&quot;password = &quot;Cryptography&quot;iv = &quot;0123456789abcdef&quot;ciphertext = encrypt_aes_cbc(plaintext, password, iv)print(f&quot;密文: &#123;ciphertext&#125;&quot;)# output 密文: H4vkfGfsU+qBEwaa7ea9gBkRcraMqbe4BGaxDb/9JG4zGleqT1VxyzGbDj/yuQn8
AES，写个解密函数 import base64from Crypto.Cipher import AESfrom Crypto.Util.Padding import unpadimport hashlibdef decrypt_aes_cbc(ciphertext, password, iv): key = hashlib.md5(password.encode()).digest()[:16] encrypted_data = base64.b64decode(ciphertext) cipher = AES.new(key, AES.MODE_CBC, iv.encode()) decrypted_data = unpad(cipher.decrypt(encrypted_data), AES.block_size) return decrypted_data.decode(&#x27;utf-8&#x27;)ciphertext = &quot;H4vkfGfsU+qBEwaa7ea9gBkRcraMqbe4BGaxDb/9JG4zGleqT1VxyzGbDj/yuQn8&quot;pasword = &quot;Cryptography&quot;iv = &quot;0123456789abcdef&quot;print(decrypt_aes_cbc(ciphertext, pasword, iv))# DASCTF&#123;A3S_CBC_M0d3_1s_1nt3r3st1ng&#125;
base64 简单数学题from Crypto.Util.number import *sum_pq = 15870713655456272818998868095126610389501417235762009793315127525027164306871912572802442396878309282140184445917718237547340279497682840149930939938364752diff_pq = 836877201325346306269647062252443025692393860257609240213263622058769344319275021861627524327674665653956022396760961371531780934904914806513684926008590e = 65537c = # 通过 sum_pq 和 diff_pq 解出 p 和 q# p + q = sum_pq# p - q = diff_pq# 所以 p = (sum_pq + diff_pq) / 2, q = (sum_pq - diff_pq) / 2p = (sum_pq + diff_pq) // 2q = (sum_pq - diff_pq) // 2n = p * qphi = (p - 1) * (q - 1)d = pow(e, -1, phi)m = pow(c, d, n)print(long_to_bytes(m))# b&#x27;DASCTF&#123;ok_1+1_1s_ez_so_try2goldbachs&#125;&#x27;
brainRSAcoppersmith from Crypto.Util.number import long_to_bytesn =  = 65537c =  =  = 507302396516541130096005191243501282527086130677192353031178107965055262527263846570902262566241833462389458229472609073835612242905629592637875830766904PR = PolynomialRing(Zmod(n), &#x27;x&#x27;)x = PR.gen()f = x^3 - h1 * x^2 + xroots = f.small_roots(X=2^512, beta=0.4, epsilon=0.01)p = int(roots[1])print(p)d = inverse_mod(e, p - 1)print(d)m = pow(c, d, p)plaintext = long_to_bytes(int(m))print(plaintext)&#x27;&#x27;&#x27;&#x27;flag&#123;V#@Ry_@ASSy_Pr013L@m-for_RsA!&#125;&#x27;&#x27;&#x27;&#x27;
