脚本循环解密 import base64, zlib, redef decode_blob(b): return zlib.decompress(base64.b64decode(b[::-1]))def peel_layers(first_payload, max_layers=200): cur = decode_blob(first_payload) n = 1 while n &lt; max_layers: s = cur.decode(&quot;utf-8&quot;, errors=&quot;replace&quot;) m = re.search(r&quot;exec\(\(_\)\(b&#x27;([^&#x27;]+)&#x27;\)\)&quot;, s) if not m: return n, cur cur = decode_blob(m.group(1).encode()) n += 1 return n, curpayload_bytes=b&#x27;&#x27;layers, final = peel_layers(payload_bytes)print(layers)print(final.decode(&quot;utf-8&quot;, errors=&quot;replace&quot;))
