然后解密数据 #!/usr/bin/env python3# -*- coding: utf-8 -*-import structRAW_HEX = &quot;&quot;&quot;000000307f4b0ef4806983f164af6f46b71d3fce1e3c0bd00c4dd162b72c156f0f3aecd2afcabf551e08380db6fd20316f8a2729&quot;&quot;&quot;KEY_HEX = &quot;ac46fb610b313b4f32fc642d8834b456&quot;SBOX_HEX = ( &quot;d690e9fecce13db716b614c228fb2c05&quot; &quot;2b679a762abe04c3aa44132649860699&quot; &quot;9c4250f491ef987a33540b43edcfac62&quot; &quot;e4b31ca9c908e89580df94fa758f3fa6&quot; &quot;4707a7fcf37317ba83593c19e6854fa8&quot; &quot;686b81b27164da8bf8eb0f4b70569d35&quot; &quot;1e240e5e6358d1a225227c3b01217887&quot; &quot;d40046579fd327524c3602e7a0c4c89e&quot; &quot;eabf8ad240c738b5a3f7f2cef96115a1&quot; &quot;e0ae5da49b341a55ad933230f58cb1e3&quot; &quot;1df6e22e8266ca60c02923ab0d534e6f&quot; &quot;d5db39b831110c5acb3e0a45e594775b&quot; &quot;8d6d484110bd09c14a890d6e97a11d16&quot; &quot;0ad9886a96d16b32023546067d65498c&quot; &quot;f03e2d7a15ff058e01843c3a3853877b&quot; &quot;0b2b7e0ff669a85ab54c1b397f088d1c&quot;)FK = [0xA3B1BAC6, 0x56AA3350, 0x677D9197, 0xB27022DC]CK = [ 0x00070E15, 0x1C232A31, 0x383F464D, 0x545B6269, 0x70777E85, 0x8C939AA1, 0xA8AFB6BD, 0xC4CBD2D9, 0xE0E7EEF5, 0xFC030A11, 0x181F262D, 0x343B4249, 0x50575E65, 0x6C737A81, 0x888F969D, 0xA4ABB2B9, 0xC0C7CED5, 0xDCE3EAF1, 0xF8FF060D, 0x141B2229, 0x30373E45, 0x4C535A61, 0x686F767D, 0x848B9299, 0xA0A7AEB5, 0xBCC3CAD1, 0xD8DFE6ED, 0xF4FB0209, 0x10171E25, 0x2C333A41, 0x484F565D, 0x646B7279,]SBOX = list(bytes.fromhex(SBOX_HEX))KEY = bytes.fromhex(KEY_HEX)def rol32(x, n): x &amp;= 0xFFFFFFFF return ((x &lt;&lt; n) &amp; 0xFFFFFFFF) | (x &gt;&gt; (32 - n))def tau_le(x): b0 = (x &gt;&gt; 24) &amp; 0xFF b1 = (x &gt;&gt; 16) &amp; 0xFF b2 = (x &gt;&gt; 8) &amp; 0xFF b3 = x &amp; 0xFF return (SBOX[b0] | (SBOX[b1] &lt;&lt; 8) | (SBOX[b2] &lt;&lt; 16) | (SBOX[b3] &lt;&lt; 24))def T(x): b = tau_le(x) return b ^ rol32(b, 2) ^ rol32(b, 10) ^ rol32(b, 18) ^ rol32(b, 24)def Tp(x): b = tau_le(x) return b ^ rol32(b, 13) ^ rol32(b, 23)def key_schedule(mk_bytes): MK = list(struct.unpack(&quot;&gt;4I&quot;, mk_bytes)) K = [MK[i] ^ FK[i] for i in range(4)] rk = [] for i in range(32): t = (K[i] ^ Tp(K[i+1] ^ K[i+2] ^ K[i+3] ^ CK[i])) &amp; 0xFFFFFFFF K.append(t) rk.append(t) return rkdef crypt_block(block16, rk32): X = list(struct.unpack(&quot;&gt;4I&quot;, block16)) for i in range(32): X.append((X[i] ^ T(X[i+1] ^ X[i+2] ^ X[i+3] ^ rk32[i])) &amp; 0xFFFFFFFF) return struct.pack(&quot;&gt;4I&quot;, X[35], X[34], X[33], X[32])def sm4_ecb(data, rk32): return b&quot;&quot;.join(crypt_block(data[i:i+16], rk32) for i in range(0, len(data), 16))def pkcs7_unpad_loose(b): pad = b[-1] return b[:-pad] if 1 &lt;= pad &lt;= 16 else bdef main(): raw = bytes.fromhex(RAW_HEX) ln = int.from_bytes(raw[:4], &quot;big&quot;) ct = raw[4:4+ln] rk_dec = key_schedule(KEY)[::-1] pt = pkcs7_unpad_loose(sm4_ecb(ct, rk_dec)) print(pt.decode(&quot;utf-8&quot;, errors=&quot;replace&quot;), end=&quot;&quot;)if __name__ == &quot;__main__&quot;: main()
