脚本如下 # -*- coding: utf-8 -*-import multiprocessingimport hashlibimport randomimport stringimport sysCHARS = string.letters + string.digitsdef cmp_md5(substr, stop_event, str_len,off, start=0, size=20): global CHARS while not stop_event.is_set(): rnds = &#x27;&#x27;.join(random.choice(CHARS) for _ in range(size)) rnds1 =rnds+ off md5 = hashlib.md5(rnds1) if md5.hexdigest()[start: start+str_len] == substr: print( rnds) stop_event.set()if __name__ == &#x27;__main__&#x27;: substr = sys.argv[1].strip() start_pos = int(sys.argv[2]) if len(sys.argv) &gt; 1 else 0 off=sys.argv[3] str_len = len(substr) cpus = multiprocessing.cpu_count() stop_event = multiprocessing.Event() processes = [multiprocessing.Process(target=cmp_md5, args=(substr, stop_event, str_len,off, start_pos)) for i in range(cpus)] for p in processes: p.start() for p in processes: p.join()
